package Prasad.mathematical;

public class EvenOrOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int n=30;
        if(n%2==0) {
        	System.out.println(n+" is even number");
        	
        }else {
        	System.out.println(n+" is odd number");
        }
	}

}
